import React, {useEffect,useState} from 'react';

const MatchDetails =  () => {
    const [matches, setMatches] = useState(null);
    const [athleteBlue, setAthleteBlue] = useState(null);

        useEffect(async () => {
            const fetchMatches = async () => {
                const response = await fetch('/api/matches');
                const json = await response.json()

                if(response.ok){
                    setMatches(json)
                }
            }
            const fetchAthleteBlue = async (matches) => {
                const result = await fetch(`/api/athletes/getbyid/${matches.athleteBlueId}`)
                .then(response => response.json())
                .then(data => {
                    setAthleteBlue(data)
                })
            }           

            await fetchMatches()
            fetchAthleteBlue(matches)
        }, [])    

    return (
        <div>
            {matches && matches.map((matches) => (
                <>
                    <div key={matches.matchNo}>{matches.matchNo}</div>
                    <div>{athleteBlue.forename}</div>
                </>
            ))}
        </div>
    );
}

export default MatchDetails;
